mapper.update:
		UPDATE room SET 
		room_selling_type = #{room_selling_type}
   	  	,room_address = #{room_address}
   	  	,room_lat_lng = point(#{room_lat},#{room_lng})
   	  	,room_lat = #{room_lat}
   	  	,room_lng = #{room_lng}
   	  	,room_title = #{room_title}
   	  	,room_content = #{room_content}
   	  	,room_type = #{room_type}


	        ,room_money_lease = #{room_money_lease}
	        ,room_lease_num = #{room_lease_num}



	        ,room_money_deposit = #{room_money_deposit}
	        ,room_money_monthly_rent = #{room_money_monthly_rent}
	        ,room_deposit_num = #{room_deposit_num}
	        ,room_monthly_rent_num = #{room_monthly_rent_num}


   	  	,room_service = #{room_service}
   	  	,room_service_charge = #{room_service_charge}
   	  	,room_park_charge = #{room_park_charge}
   	  	,room_width = #{room_width}
   	  	,room_keywords = #{room_keywords}
   	  	,room_floor_str = #{room_floor_str}
   	  	,room_total_floor = #{room_total_floor}
   	  	,room_elevator = #{room_elevator}
   	  	,room_heating = #{room_heating}
   	  	,room_balcony = #{room_balcony}
   	  	,room_loan = #{room_loan}
   	  	,room_move_in_date = #{room_move_in_date}
   	  	,room_option = #{room_option}
   	  	,room_author_email = #{room_author_email}


	        ,room_img_url = #{room_img_url}


	        ,room_complex_id = #{room_complex_id}


	        ,room_dong = #{room_dong}


	        ,room_ho = #{room_ho}

		WHERE room_number = #{room_number};

mapper.select:
select room_number
from room


		room_lat = #{room_lat}


		AND room_lng = #{room_lng}


		AND room_complex_id = #{room_complex_id}


        AND room_dong = #{room_dong}


        AND room_ho = #{room_ho}


        AND room_floor_str = #{room_floor_str}


        AND room_total_floor = #{room_total_floor}

select room_img_number, room_img
from room_img

	room_img_number = #{item.room_number}

SELECT room_number, room_lat, room_lng, room_selling_type, room_type, room_address, room_title, room_content
, room_money_lease
, room_money_deposit
, room_money_monthly_rent
, room_dong
, room_ho
, room_complex_id
, room_service, room_service_charge, room_park_charge, room_width, room_keywords, room_img_url, room_floor_str, room_total_floor ,room_elevator, room_heating, room_balcony, room_loan, room_move_in_date, room_option, room_author_email ,room_create_date, room_status, room_likes, room_hits, room_deposit_num, room_lease_num, room_monthly_rent_num FROM room
AND ST_Distance( room_lat_lng, ST_GeomFromText(#{point}) ) <= #{distance}
AND #{lat} = room_lat AND #{lng} = room_lng
AND room_number = #{room_number}
AND room_selling_type = '전세'
AND room_selling_type = '월세'
#{item}
AND room_lease_num >= #{minlease}
AND room_lease_num <= #{maxlease}
AND room_deposit_num >= #{mindeposit}
AND room_deposit_num <= #{maxdeposit}
AND room_monthly_rent_num >= #{minrent}
AND room_monthly_rent_num <= #{maxrent}
AND room_number in
#{number.value}
group by room_number
AND room_author_email = #{member_email}
order by 1=1
,ST_Distance( room_lat_lng, ST_GeomFromText(#{point}) )
, field
#{number.value}
,room_lease_num desc
,room_deposit_num desc ,room_monthly_rent_num desc
